mapper.insert:

  SELECT LAST_INSERT_ID()

insert into dynamic (module_id, data_id, time
  )
values (#{moduleId,jdbcType=SMALLINT}, #{dataId,jdbcType=BIGINT}, #{time,jdbcType=VARCHAR}
  )

mapper.update:
update dynamic
set module_id = #{moduleId,jdbcType=SMALLINT},
  data_id = #{dataId,jdbcType=BIGINT},
  time = #{time,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select count(id)
from dynamic
where 1=1

	and module_id = #{moduleId,jdbcType=SMALLINT}


	and data_id = #{dataId,jdbcType=BIGINT}


	and time >= #{startTime,jdbcType=VARCHAR}


	and time <= #{endTime,jdbcType=VARCHAR}

  select 
  
  from dynamic
  where 1=1
  
  	and module_id = #{moduleId,jdbcType=SMALLINT}
  
  
  	and data_id = #{dataId,jdbcType=BIGINT}
  
  
  	and time >= #{startTime,jdbcType=VARCHAR}
  
  
  	and time <= #{endTime,jdbcType=VARCHAR}
  
  order by time desc
  
  
		limit #{start},#{max}

mapper.delete:
	delete from dynamic 
	where module_id = #{moduleId,jdbcType=SMALLINT}
  and data_id = #{dataId,jdbcType=BIGINT}

  	delete from dynamic 
  	where module_id = #{moduleId,jdbcType=SMALLINT}
    and data_id in
    
	     #{id}
